'use strict';

/**
* @description Service that allows to save a slide of a project on server
* @param {json} data - json encoded data of a slide autogenerated from fabric.js
* @param {integer} idPrj - identify the project that contains the Presentation
* @param {integer} idPrs - identify the project that contains the Slide
* @param {integer} idSlide - identify the the Slide
*/
angular.module('app.services.signup', ['ngRoute', 'ngResource'])

    .factory('signupService', ['$scope', '$http', '$resource', function ($scope, $http, $resource) {

        return $resource('/auth/register', {}, {
            signup:{
                method: 'POST',
                params: {
                    // email: "fabioros@gmail.com",
                    // username: "fabioros",
                    // firstName: "Fabio",
                    // lastName: "Ros",
                    // password: "fabioros"

                    email: $scope.signup_data.email,
                    username: $scope.signup_data.username,
                    firstName: $scope.signup_data.firstName,
                    lastName: $scope.signup_data.lastName,
                    password: $scope.signup_data.password
                }
            }
        });

    }]);
